mapper.update:
update board
set write_date = sysdate


    ,title = #{title}


    ,content = #{content}


    ,secret_flag = #{secretFlag}


    ,board_file = #{boardFile}

where board_no = #{boardNo}

mapper.select:
select *
from board
where write_date = (
select min(write_date)
from board

        WHERE write_date > #{writeDate}

)

and write_date > #{writeDate}

and lower(board_category) like #{boardCategory}
order by write_date desc

select book_no
from site_book
where member_id = #{memberId}
